import { api_result } from "../_types/types";
import getConfig from "next/config";

const { publicRuntimeConfig } = getConfig();
const { API_URL } = publicRuntimeConfig;

const authHeader = () => {
    return { Authorization: `Bearer ` };
};

const get = async (api: string): Promise<api_result> => {
    const requestOptions = {
        method: "GET",
        headers: authHeader(api),
    };
    return fetch(api, requestOptions).then(handleResponse);
};

const post = async (api, body): Promise<api_result> => {
    const requestOptions = {
        method: "POST",
        headers: { "Content-Type": "application/json", ...authHeader(api) },
        credentials: "include",
        body: JSON.stringify(body),
    };
    return fetch(`${api}`, requestOptions as RequestInit).then(handleResponse);
};

function put(api, body) {
    const requestOptions = {
        method: "PUT",
        headers: { "Content-Type": "application/json", ...authHeader(api) },
        body: JSON.stringify(body),
    };
    return fetch(`${api}`, requestOptions).then(handleResponse);
}

function handleResponse(response: Response) {
    return response.text().then((text) => {
        if (text.startsWith("<!DOCTYPE html>"))
            return Promise.reject({ code: response.status });
        const data = text && JSON.parse(text);
        if (!response.ok) {
            if (
                [401, 403].includes(response.status) &&
                accountService.userValue
            ) {
                accountService.logout();
            }
            const error = (data && data.message) || response.statusText;
            return Promise.reject(error);
        }
        return data;
    });
}

