/** @jsx jsx */
import { jsx } from "theme-ui";
import { useRouter } from "next/router";

const EpisodeComponent = () => {
    const router = useRouter();
    const { id } = router.query;

    return (
        <div
            className="col-12"
            sx={{ variant: "containers.card", bg: "white" }}
        >
            <div className="row">
                <div
                    className="col-3"
                    sx={{ maxHeight: "250px", overflow: "hidden" }}
                >
                    <img
                        className="img-fluid"
                        sx={{
                            height: "auto",
                            width: "100%",
                            display: "grid",
                            alignSelf: "center",
                        }}
                        src={data.image && data.image.original}
                    />
                </div>
                <div className="col-9">
                    <div className="row">
                        <div
                            className="col-10"
                            sx={{
                                display: "grid",
                                placeItems: "center start",
                            }}
                        >
                            <h1
                                sx={{
                                    display: "grid",
                                    placeItems: "center start",
                                    marginBlockEnd: "0",
                                }}
                            >
                                {data.name}
                            </h1>
                        </div>
                        <div className="col-2">
                            <span
                                sx={{
                                    fontSize: "18px",
                                    display: "grid",
                                    placeItems: "center",
                                    height: "100%",
                                }}
                            >
                                {/* <Badge count={data.rating.average} /> */}
                                <Tag color="#f50">
                                    Rating: {data.rating.average}
                                </Tag>
                            </span>
                        </div>
                        <div className="col-12 mt-3">
                            <p sx={{ fontSize: "16px" }}>
                                {ReactHtmlParser(data.summary)}
                            </p>
                        </div>
                        <div className="col-12">
                            <p
                                sx={{
                                    color: "blue",
                                    textDecoration: "underline",
                                    fontSize: "16px",
                                    "&:hover": { cursor: "pointer" },
                                    marginBlockEnd: "5px",
                                }}
                            >
                                {data.officialSite}
                            </p>
                        </div>
                        <div className="col-12 mt-3">
                            <div className="row">
                                <div className="col-3">
                                    <Tag
                                        color="magenta"
                                        sx={{
                                            paddingY: "5px",
                                            display: "grid",
                                            placeItems: "center",
                                        }}
                                    >
                                        <p
                                            sx={{
                                                fontSize: "16px",
                                                marginBlockEnd: "0",
                                            }}
                                        >
                                            Network: {data.network.name}
                                        </p>
                                    </Tag>
                                </div>
                                <div className="col-3">
                                    <Tag
                                        color="green"
                                        sx={{
                                            paddingY: "5px",
                                            display: "grid",
                                            placeItems: "center",
                                        }}
                                    >
                                        <p
                                            sx={{
                                                fontSize: "16px",
                                                marginBlockEnd: "0",
                                            }}
                                        >
                                            Status: {data.status}
                                        </p>
                                    </Tag>
                                </div>
                                <div className="col-3">
                                    <Tag
                                        color="cyan"
                                        sx={{
                                            paddingY: "5px",
                                            display: "grid",
                                            placeItems: "center",
                                        }}
                                    >
                                        <p
                                            sx={{
                                                fontSize: "16px",
                                                marginBlockEnd: "0",
                                            }}
                                        >
                                            Genre: {data.genres[0]}
                                        </p>
                                    </Tag>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default EpisodeComponent;
