import React, { useCallback, useEffect, useReducer, useState } from "react";
import { ThemeProvider } from "theme-ui";
import theme from "../../theme";
import Nav from "../components/nav.component";
import "bootstrap/dist/css/bootstrap.min.css";
import "antd/dist/antd.css";
import "../../style.scss";
import "../components/customElements/tabHead.scss";
import useSWR, { SWRConfig } from "swr";
import { CRUDService } from "../_services/CRUD.service";
import { Layout } from "antd";
import { accountService } from "src/_services/index.service";
import { useEffectOnce } from "react-use";
import {
    ProfileProvider,
    ProfileReducer,
    initialProfileState,
} from "src/_contexts/profile.context";
import { UPDATE } from "src/_types/types";

const { Content } = Layout;

export default function App({ Component, pageProps }) {
    const [currentUser, setCurrentUser] = useState(null);
    const [loading, setLoading] = useState(true);
    let [ProfileState, dispatchProfile] = useReducer(
        ProfileReducer,
        initialProfileState
    );

    useEffectOnce(() => {
        accountService
            .refreshToken()
            .then((x) => {
                setCurrentUser(x.data);
            })
            .catch((err) => {})
            .finally(() => {
                setLoading(false);
            });
    });

    useEffect(() => {
        currentUser && onLoadUserData();
    }, [currentUser]);

    const onLoadUserData = useCallback(async () => {
        if (currentUser) {
            const res = await accountService.getProfile(currentUser.id);
            dispatchProfile({ type: UPDATE, payload: res.data });
        }
    }, [currentUser]);

    return (
        <ProfileProvider value={{ ProfileState, dispatchProfile }}>
            <ThemeProvider theme={theme}>
                <SWRConfig value={{ fetcher: CRUDService.get }}>
                    {!loading ? (
                        <Layout>
                            <Nav />
                            <Content
                                sx={{
                                    height: "100%",
                                    backgroundColor: `background`,
                                    paddingBlockEnd: "80px",
                                }}
                                className="site-layout mainContainer col-12"
                            >
                                <div className="row">
                                    <div
                                        sx={{
                                            variant: "containers.page",
                                        }}
                                        className="site-layout-background col-12"
                                    >
                                        <Component {...pageProps} />
                                    </div>
                                </div>
                            </Content>
                        </Layout>
                    ) : (
                        <div>Loading...</div>
                    )}
                </SWRConfig>
            </ThemeProvider>
        </ProfileProvider>
    );
}
