import { createContext } from 'react';
import { IProfile } from 'src/_models/profile.model';
import { UPDATE } from 'src/_types/types';

const ProfileReducer = (state: IProfile, action: { type: string, payload: any }): IProfile => {
    switch (action.type) {
        case UPDATE:
            return { ...action.payload }
        default:
            break;
    }
    return state;
}

const initialProfileState: IProfile = null as unknown as IProfile;

const ProfileContext = createContext({
    ProfileState: initialProfileState,
    dispatchProfile: {} as any
})

const ProfileProvider = ProfileContext.Provider;
export default ProfileContext;
export { ProfileProvider, ProfileReducer, initialProfileState }
